var helper = require('./helper');


exports.runAfter = function runAfter(job, _after) {
 
    setInterval(job, helper.convertToTime(_after));
}


exports.runOn = function runOn(job, _on) {
    
    helper.convertToPromise(_on)
    .then(function done(prom) {
       
       //console.log(prom);
       job();
       runOn(job, _on);
        
    }, function overflow(_interval) {
       
       //console.log("Overflow !");
       //runOn(job, _on);
    });
}
